mapper.select:
select

from sys_menu a


		AND a.menu_id = #{id}


		AND a.parent_id = #{parentId}


		AND a.name = #{name}


		AND a.url = #{url}


		AND a.perms = #{perms}


		AND a.type = #{type}


		AND a.icon = #{icon}


		AND a.order_num = #{orderNum}


		AND a.create_by = #{createBy}


		AND a.create_time = #{createTime}


		AND a.update_by = #{updateBy}


		AND a.update_time = #{updateTime}

select

from sys_menu a

	a.del_flag = #{delFlag}

		AND a.menu_id = #{id}


		AND a.parent_id = #{parentId}


		AND a.name = #{name}


		AND a.url = #{url}


		AND a.perms = #{perms}


		AND a.type = #{type}


		AND a.icon = #{icon}


		AND a.order_num = #{orderNum}


		AND a.create_by = #{createBy}


		AND a.create_time = #{createTime}


		AND a.update_by = #{updateBy}


		AND a.update_time = #{updateTime}




		order by ${sort} ${order}


		order by a.create_time desc



	limit #{offset}, #{limit}

select count(*) from sys_menu a

	a.del_flag = #{delFlag}

		AND a.menu_id = #{menuId}


		AND a.parent_id = #{parentId}


		AND a.name = #{name}


		AND a.url = #{url}


		AND a.perms = #{perms}


		AND a.type = #{type}


		AND a.icon = #{icon}


		AND a.order_num = #{orderNum}


		AND a.create_by = #{createBy}


		AND a.create_time = #{createTime}


		AND a.update_by = #{updateBy}


		AND a.update_time = #{updateTime}
